import React from "react";
import oneinrlogo from "../Images/one-inr.png";
import { useHistory } from "react-router";
import { Link, NavLink } from "react-router-dom";
import Footer from "./Footer";

function Navbar() {
  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light d-flex">
        <div className="logoHeader d-flex">
          <div className="logo">
            <Link to="/homepage">
              <img
                src={oneinrlogo}
                className="logo-img mt-5 "
                alt="bubbledoclogo"
              />
            </Link>
          </div>
          <button
            className="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
        </div>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav ml-auto mr-5">
            <li className="nav-item">
              <NavLink
                exact
                activeClassName="nav-link ActiveItem"
                exact
                to="/homepage"
              >
                Home
              </NavLink>
            </li>
            <li className="nav-item">
              {/* <NavLink
                exact
                activeClassName="nav-link ActiveItem"
                exact
                to="/about"
              > */}
              About Us
              {/* </NavLink> */}
            </li>
            <li className="nav-item">
              {/* <NavLink
                exact
                activeClassName="nav-link ActiveItem"
                exact
                to="/about"
              > */}
              Campaigns
              {/* </NavLink> */}
            </li>
            <li className="nav-item">
              {/* <NavLink
                exact
                activeClassName="nav-link ActiveItem"
                exact
                to="/contact"
              > */}
              Contact Us
              {/* </NavLink> */}
            </li>
            {/* {user ? (
              <>
                <li className="nav-item dropdown">
                  <a
                    className="nav-link dropdown-toggle"
                    href="#"
                    id="navbarDropdown"
                    role="button"
                    data-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                  >
                    {user.name}
                  </a>
                  <div className="dropdown-menu" aria-labelledby="navbarDropdown">
                    <ul className="mega-links" style={{ marginBottom: "1rem" }}>
                      <li className='subItems'>
                        <Link to="/account" >
                          My Profile
                        </Link>
                      </li>
                      <li className='subItems'>
                        <Link to="/wishlist" >
                          Wishlist
                        </Link>
                      </li>
                      <li className='subItems'>
                        <Link
                          to="/openOrders"
                          
                        >
                          Open Orders
                        </Link>
                      </li>
                      <li className='subItems'>
                        <Link to="/orders" >
                          My Orders
                        </Link>
                      </li>
                      <li className='subItems'>
                        <Link
                          to="#"
                          onClick={onCheckout}
                          
                        >
                          Checkout
                        </Link>
                      </li>
                    </ul>
                  </div>
                </li>
                <li className="nav-item">
                  <NavLink exact activeClassName="nav-link ActiveItem"
                    to=""
                    
                    onClick={logout}
                  >
                    Logout
                  </NavLink>
                </li>
              </>
            ) : (
              <li className="nav-item">
                <NavLink exact activeClassName="nav-link ActiveItem"  to="/login" >
                  SIGN IN
                </NavLink>
              </li>
            )}
            <li className="icons nav-item">
              <Link to="/cart" >
                <span>
                  <i className="fas fa-shopping-cart" style={cartIcon}></i>
                  <small
                    className="count d-flex"
                    style={{ backgroundColor: "#1e46a1" }}
                  >
                    {cart && cart.cartData && cart.cartData.length > 0
                      ? cart.cartData.length
                      : "0"}
                  </small>
                </span>
              </Link>
            </li> */}
          </ul>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
