import React from "react";
import { useForm } from "react-hook-form";

const RadioApp = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => alert(JSON.stringify(data));

  //   handle company condition to appear
  const tradeType = watch("tradeType");
  // console.log(tradeType);
  return (
    <React.Fragment>
      <section>
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="bg-gray-200 mx-5 mt-20 rounded-lg h-96 relative md:w-4/5 md:mx-auto lg:w-3/5 lg:mx-auto">
            <div className="mx-5 pt-5">
              <div className="grid grid-cols-3 gap-5 ">
                <div className=" w-full h-12 ">
                  <div className="flex justify-end items-center h-12">
                    <p>Trade Type</p>
                  </div>
                </div>
                <div className=" col-span-2 flex space-x-8">
                  <div className="flex items-center h-12 space-x-2">
                    <input
                      type="radio"
                      value="individual"
                      {...register("tradeType", {
                        required: "Trade type is required",
                      })}
                    />
                    <p>Individual</p>
                  </div>

                  <div className="flex items-center h-12 space-x-2">
                    <input
                      type="radio"
                      value="company"
                      {...register("tradeType", {
                        required: "Trade type is required",
                      })}
                    />
                    <p>Company</p>
                  </div>
                </div>
              </div>

              {/* trade type error */}
              <div className="absolute top-12 left-80">
                {errors.tradeType && (
                  <span className="text-red-400 text-sm">
                    {errors.tradeType.message}
                  </span>
                )}
              </div>

              {/* company section */}
              <div>
                {tradeType === "company" && (
                  <div className="grid grid-cols-3 gap-5 mt-5">
                    <div className=" w-full h-12 ">
                      <div className="flex justify-end items-center h-12">
                        <p>Company</p>
                      </div>
                    </div>
                    <div className=" col-span-2 flex space-x-8 ">
                      <div className="flex items-center w-full h-12 md:w-2/3 ">
                        <div>
                          <input
                            type="text"
                            placeholder="Company name"
                            className="w-full rounded-lg "
                            {...register("companyName", {
                              required: "Company name is required",
                            })}
                          />
                          {errors.companyName && (
                            <span className="text-red-400 text-sm">
                              {errors.companyName.message}
                            </span>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>

              {/* first name section */}
              <div className="grid grid-cols-3 gap-5 mt-5">
                <div className=" w-full h-12 ">
                  <div className="flex justify-end items-center h-12">
                    <p>First Name</p>
                  </div>
                </div>
                <div className=" col-span-2 flex space-x-8">
                  <div className="flex items-center w-full h-12  md:w-2/3 ">
                    <div>
                      <input
                        type="text"
                        placeholder="First name"
                        className="w-full rounded-lg"
                        {...register("firstName", {
                          required: "First name is required",
                        })}
                      />
                      {errors.firstName && (
                        <span className="text-red-400 text-sm">
                          {errors.firstName.message}
                        </span>
                      )}
                    </div>
                  </div>
                </div>
              </div>

              {/* last name section */}
              <div className="grid grid-cols-3 gap-5 mt-5">
                <div className=" w-full h-12 ">
                  <div className="flex justify-end items-center h-12">
                    <p>Last Name</p>
                  </div>
                </div>
                <div className=" col-span-2 flex space-x-8">
                  <div className="flex items-center w-full h-12 md:w-2/3  ">
                    <div>
                      <input
                        type="text"
                        placeholder="Last name"
                        className="w-full rounded-lg"
                        {...register("lastName", {
                          required: "Last name is required",
                        })}
                      />
                      {errors.lastName && (
                        <span className="text-red-400 text-sm">
                          {errors.lastName.message}
                        </span>
                      )}
                    </div>
                  </div>
                </div>
              </div>

              {/* button section */}
              <div className="grid grid-cols-3 gap-5 mt-5">
                <div className=" w-full h-12 ">
                  <div className="flex justify-end items-center h-12"></div>
                </div>
                <div className=" col-span-2 flex space-x-8">
                  <div className="flex items-center w-full h-12 ">
                    <input
                      type="submit"
                      value="Submit"
                      className="w-2/3 h-10 rounded-lg bg-red-500 text-white font-semibold md:w-1/3 "
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </form>
      </section>
    </React.Fragment>
  );
};

export default RadioApp;
