import React, { useState } from "react";
import CssBaseline from "@material-ui/core/CssBaseline";
import { DataGrid } from "@material-ui/data-grid";
import { makeStyles } from "@material-ui/core/styles";
import { Paper, Typography, TextField } from "@material-ui/core";
// import { getProducts } from "../../Helper/helper";
import cogoToast from "cogo-toast";
// Modal
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";
import OnHoverText from "../../reusable/OnHoverText";

import DeleteCategory from "./../category/DeleteCategory";
import { deleteProduct } from "./../../Helper/helper";
import EditProduct from "./EditProduct";
import Loader from "../../../reusable/Loader";

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
  extendedIcon: {
    marginRight: theme.spacing(1),
  },
  border: {
    border: "2px solid grey",
    fontSize: "20px",
  },
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    width: "400px",
    height: "400px",
    boxShadow: theme.shadows[5],
  },
}));

function Campaigns() {
  const [rows, setRows] = useState([]);
  const [allProduct, setAllProducts] = useState([]);
  const [imgSrc, setImgSrc] = useState("");
  const [deleteProd, setDelete] = useState(false);
  const [edit, setEdit] = useState(false);
  const [loading, setLoading] = useState(true);
  const initialData = {
    id: "",
    name: "",
  };
  const [editData, setEditData] = useState(initialData);
  const [deleteData, setDeleteData] = useState(initialData);
  const [count, setcount] = useState();
  const [to, setTo] = useState(100000);
  const [from, setFrom] = useState(1);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const classes = useStyles();

  const getRows = (allProducts) => {
    const asd = allProducts.map((product, index) => {
      return {
        productImage: product.productImage1,
        id: product.id,
        categoryName: product.category.CategoryName,
        subCategoryId: product.subCategoryId,
        productTitle: product.productTitle,
        productSku: product.productSku,
        productPrice1: product.offerPrice,
        productPrice: product.totalPrice,
        edit: product.id,
        productOrigin: product.productOrigin.originName,
      };
    });

    return asd;
  };
  const imageModal = (imgSrc) => {
    setImgSrc(imgSrc);
    handleOpen();
  };
  const fetchProduct = async () => {
    const res = await getProducts(from, to);

    setAllProducts(res.results.data.Product);
    setRows(getRows(res.results.data.Product));
    setcount(res.results.data.count);
    setLoading(false);
  };

  React.useEffect(() => {
    if (!deleteProd) fetchProduct();
  }, [deleteProd]);

  const onDelete = () => {
    setDelete(!deleteProd);
  };

  //   const handleChangeRowsPerPage = async(e) => {
  //     setTo(e.page * e.pageSize + e.pageSize);
  //    setFrom( e.page * e.pageSize + 1 ) ;
  //    const firstIndex = Number(e.page * e.pageSize + 1) ;
  //    const lastIndex =Number(e.page * e.pageSize + e.pageSize)

  //    const res = await getProducts(firstIndex,lastIndex);
  //    setAllProducts(res.results.data.Product);
  //    setRows(getRows(res.results.data.Product));
  //  };

  const onDeleteFunction = async () => {
    const id = deleteData.id;
    const res = await deleteProduct(id);
    if (!res.error) {
      cogoToast.success("Delected Successfully!");
      await fetchProduct();
    }
  };

  const onEdit = () => {
    setEdit(!edit);
  };
  const searchName = async (e) => {
    if (e.target.value) {
      const res = await getProducts(from, to, e.target.value);

      setAllProducts(res.results.data.Product);
      setRows(getRows(res.results.data.Product));
    } else {
      fetchProduct();
    }
  };

  const columns = [
    {
      field: "productImage",
      headerName: "Image",
      width: 170,
      height: "100px",
      headerAlign: "center",
      sortable: "false",
      renderCell: (params) => (
        <img
          src={params.value}
          alt={"no pic"}
          height="100%"
          width="60%"
          onClick={(e) => imageModal(params.value)}
        />
      ),
    },
    { field: "id", headerName: "ID", width: 100 },
    {
      field: "productTitle",
      headerName: "Name",
      width: 200,
      renderCell: (params) => (
        <OnHoverText textToHover={params.value} popoverText={params.value} />
      ),
    },

    { field: "productSku", headerName: "SKU", width: 200 },
    {
      field: "productPrice",
      headerName: "M.R.P.",

      width: 100,
    },
    {
      field: "productPrice1",
      headerName: "Offer Price",

      width: 130,
    },

    { field: "productOrigin", headerName: "Product Origin", width: 180 },
    {
      field: "edit",
      headerName: "Action",
      width: 100,
      flex: 1,
      renderCell: (params) => (
        <>
          <EditIcon
            size="medium"
            variant="contained"
            color="primary"
            onClick={() => {
              setEditData({
                id: params.value,
                subCategoryId: params.row.subCategoryId,
              });
              onEdit();
            }}
          ></EditIcon>
          <DeleteIcon
            size="medium"
            variant="contained"
            style={{ marginLeft: "10px" }}
            color="secondary"
            onClick={() => {
              setDeleteData({
                id: params.value,
                header: "Product",
              });

              onDelete();
            }}
          ></DeleteIcon>
        </>
      ),
    },
  ];

  return loading ? (
    <center style={{ marginTop: "15%" }}>
      {" "}
      <Loader />
    </center>
  ) : (
    <React.Fragment>
      <CssBaseline />
      {/* Image modal  */}
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <img
              src={imgSrc}
              alt="Product"
              style={{ width: "100%", height: "100%" }}
            />
          </div>
        </Fade>
      </Modal>

      {/* Image modal close */}
      <Paper variant="outlined">
        <div
          className="row"
          style={{ justifyContent: "space-between", paddingTop: "20px" }}
        >
          <div className="col-9" style={{ paddingLeft: "30px" }}>
            <Typography style={{ fontWeight: "600" }} variant="h5">
              Products List
            </Typography>
          </div>
          <div
            className="col-md-3"
            style={{
              marginBottom: "10px",
              marginTop: "-10px",
            }}
          >
            <TextField
              label="Search Products"
              type="search"
              onChange={(e) => searchName(e)}
            />{" "}
          </div>
        </div>
        {rows && rows.length ? (
          <div style={{ height: "850px", width: "100%" }}>
            <DataGrid
              // paginationMode="server"
              rowCount={count}
              // onPageChange={handleChangeRowsPerPage}
              rows={rows}
              columns={columns}
              pageSize={10}
              rowHeight={70}
              disableSelectionOnClick={false}
              sortable={false}
            />
          </div>
        ) : (
          <center>
            {" "}
            <h1 style={{ marginTop: "15%", marginBottom: "20%" }}>
              NO PRODUCTS FOUND
            </h1>
          </center>
        )}
      </Paper>

      <DeleteCategory
        deleteopen={deleteProd}
        deletetoggle={onDelete}
        deleteData={deleteData}
        getData={onDeleteFunction}
      />

      <EditProduct
        editOpen={edit}
        editToggle={onEdit}
        editData={editData}
        getData={fetchProduct}
      />
    </React.Fragment>
  );
}

export default Campaigns;
