import React, { useEffect, useState } from "react";
// import { useHistory } from "react-router";
// import { useSelector } from "react-redux";
import bubbledoclogo from "../../assets/images/Bubble Doc logo2.jpg";
import { Link, NavLink } from "react-router-dom";
import {
  BASE_URL,
  CATEGORIES,
  CART,
  TEMPORDER,
} from "./../../resources/APIEndpoints";
import APIService from "../../services/APIService";
// import { useDispatch } from "react-redux";
// import { fetchCartItems } from "../Redux/actions/cartActions";
import APIServices from "../../services/APIServices";

function Navbar(props) {
  const history = useHistory();
  const dispatch = useDispatch();
  const [category, setCategory] = useState("");
  const [cart, setCart] = useState("");
  const [tempId, setTempId] = useState("");

  const onCheckout = async () => {
    const url = BASE_URL + TEMPORDER;
    const res = await new APIServices().post(url);

    setTempId(res.results.tempOrderId);

    history.push({
      pathname: "/checkout",
      state: res.results.tempOrderId,
    });
  };

  const cartIcon = {
    fontSize: "1.8rem",
    color: "#696969",

    right: "4%",
    bottom: "10%",
  };

  const fetchCategory = async () => {
    const url = BASE_URL + CATEGORIES + `all-category`;
    const res = await new APIService().get(url);

    setCategory(res.results);
  };
  const fetchCart = async () => {
    const url = BASE_URL + CART;
    const res = await new APIService().get(url);

    setCart(res.results.data);
  };

  useEffect(() => {
    setTimeout(() => {
      fetchCart();
    }, 400);
  }, [props.fetchCart]);

  useEffect(() => {
    fetchCategory();
  }, []);

  const auth = useSelector((state) => state.auth);

  const { authenticate } = auth;
  const user = JSON.parse(localStorage.getItem("user"));
  const logout = () => {
    window.localStorage.clear();

    history.push("/");
    window.location.reload();
  };
  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light d-flex">
        <div className="logoHeader d-flex">
          <div className="logo">
            <Link to="/homepage">
              <img
                src={bubbledoclogo}
                className="logo-img mt-5 "
                alt="bubbledoclogo"
              />
            </Link>
          </div>
          <button
            className="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
        </div>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav ml-auto mr-5">
            <li className="nav-item">
              <NavLink
                exact
                activeClassName="nav-link ActiveItem"
                exact
                to="/homepage"
              >
                HOME
              </NavLink>
            </li>
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="#"
                id="navbarDropdown"
                role="button"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
              >
                CATEGORIES
              </a>
              <div className="dropdown-menu" aria-labelledby="navbarDropdown">
                <ul className="mega-links " style={{ marginBottom: "1rem" }}>
                  {category.data &&
                    category.data.length > 0 &&
                    category.data
                      .sort((a, b) => (a.id > b.id ? 1 : -1))
                      .map((el) => {
                        return (
                          <li className="subItems" key={el.id}>
                            <Link
                              to={{
                                pathname: `/productbysubcategories/${el.id}`,
                                state: { data: el.id },
                              }}
                            >
                              {el.categoryName}
                            </Link>
                          </li>
                        );
                      })}
                </ul>
              </div>
            </li>
            <li className="nav-item">
              <NavLink
                exact
                activeClassName="nav-link ActiveItem"
                exact
                to="/about"
              >
                ABOUT
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink
                exact
                activeClassName="nav-link ActiveItem"
                exact
                to="/contact"
              >
                CONTACT US
              </NavLink>
            </li>
            {user ? (
              <>
                <li className="nav-item dropdown">
                  <a
                    className="nav-link dropdown-toggle"
                    href="#"
                    id="navbarDropdown"
                    role="button"
                    data-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                  >
                    {user.name}
                  </a>
                  <div
                    className="dropdown-menu"
                    aria-labelledby="navbarDropdown"
                  >
                    <ul className="mega-links" style={{ marginBottom: "1rem" }}>
                      <li className="subItems">
                        <Link to="/account">My Profile</Link>
                      </li>
                      <li className="subItems">
                        <Link to="/wishlist">Wishlist</Link>
                      </li>
                      <li className="subItems">
                        <Link to="/openOrders">Open Orders</Link>
                      </li>
                      <li className="subItems">
                        <Link to="/orders">My Orders</Link>
                      </li>
                      <li className="subItems">
                        <Link to="#" onClick={onCheckout}>
                          Checkout
                        </Link>
                      </li>
                    </ul>
                  </div>
                </li>
                <li className="nav-item">
                  <NavLink
                    exact
                    activeClassName="nav-link ActiveItem"
                    to=""
                    onClick={logout}
                  >
                    Logout
                  </NavLink>
                </li>
              </>
            ) : (
              <li className="nav-item">
                <NavLink
                  exact
                  activeClassName="nav-link ActiveItem"
                  to="/login"
                >
                  SIGN IN
                </NavLink>
              </li>
            )}
            <li className="icons nav-item">
              <Link to="/cart">
                <span>
                  <i className="fas fa-shopping-cart" style={cartIcon}></i>
                  <small
                    className="count d-flex"
                    style={{ backgroundColor: "#1e46a1" }}
                  >
                    {cart && cart.cartData && cart.cartData.length > 0
                      ? cart.cartData.length
                      : "0"}
                  </small>
                </span>
              </Link>
            </li>
          </ul>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
